let table;
let img_table;
let img_index;
let font;
let intro;
let a;

export default function App() {

  const preload = p5 => {
    table = loadTable("assets/DTA_data.csv", "csv", "header");
    font = "Carrois Gothic SC"
  }
  const setup = (p5, canvasParentRef) => {
    // array of the image links loaded into images
  img_table = [];

  //cycle through the table
  for (let r = 0; r < table.getRowCount(); r++) {
    // get each image link from column 3
    img_table.push(loadImage(table.getString(r, 3)));
  }

  // establish intro page being loaded before other context
  intro = true;

  // canvas size equivalent to array length so that each pixel location on the x axis corresponds with an image in the array
  createCanvas(img_table.length, img_table.length);

  // text formatting
  textAlign(CENTER, CENTER);
  textFont(font);
  textSize(15);

  // data source link formatting
    a = createA("https://archive.org/details/digitaltransgenderarchive", "data source: internet archive", "_blank"); // _blank makes the link open in another tab
  a.style('font-family', "Carrois Gothic SC");
  a.style('color', 'white');
  }

  const draw = p5 => {
    p5.background(255, 130, 20)
    p5.ellipse(100, 100, 100)
    p5.ellipse(300, 100, 100)
  }

  return <Sketch preload={preload} setup={setup} draw={draw} />
}
